mapper.update:
UPDATE ICLOUD_APPLICATIONAGENCY
USER_ID = #{userId,jdbcType=BIGINT}, NAME = #{name,jdbcType=VARCHAR}, AGENCY_TYPE = #{agencyType,jdbcType=VARCHAR}, AGENCY_LEVEL = #{agencyLevel,jdbcType=VARCHAR}, AGENCY_AREA = #{agencyArea,jdbcType=VARCHAR}, QUALITY_IMAGE_SHARE_URL = #{qualityImageShareUrl,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=INTEGER}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR}, ORGANIZATION_ID = #{organizationId,jdbcType=BIGINT}, SENDED = #{sended,jdbcType=VARCHAR}, UPDATED = #{updated,jdbcType=VARCHAR}, APPROVAL_OPINION = #{approvalOpinion,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE ICLOUD_APPLICATIONAGENCY
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM ICLOUD_APPLICATIONAGENCY
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM ICLOUD_APPLICATIONAGENCY


		AND ID = #{id,jdbcType=INTEGER}


		AND USER_ID = #{userId,jdbcType=BIGINT}


		AND NAME = #{name,jdbcType=VARCHAR}


		AND AGENCY_TYPE = #{agencyType,jdbcType=VARCHAR}


		AND AGENCY_LEVEL = #{agencyLevel,jdbcType=VARCHAR}


		AND AGENCY_AREA = #{agencyArea,jdbcType=VARCHAR}


		AND QUALITY_IMAGE_SHARE_URL = #{qualityImageShareUrl,jdbcType=VARCHAR}


		AND STATUS = #{status,jdbcType=INTEGER}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND ORGANIZATION_ID = #{organizationId,jdbcType=BIGINT}


		AND SENDED = #{sended,jdbcType=VARCHAR}


		AND UPDATED = #{updated,jdbcType=VARCHAR}


		AND APPROVAL_OPINION = #{approvalOpinion,jdbcType=VARCHAR}


  FROM ICLOUD_APPLICATIONAGENCY


		AND ID = #{id,jdbcType=INTEGER}


		AND USER_ID = #{userId,jdbcType=BIGINT}


		AND NAME = #{name,jdbcType=VARCHAR}


		AND AGENCY_TYPE = #{agencyType,jdbcType=VARCHAR}


		AND AGENCY_LEVEL = #{agencyLevel,jdbcType=VARCHAR}


		AND AGENCY_AREA = #{agencyArea,jdbcType=VARCHAR}


		AND QUALITY_IMAGE_SHARE_URL = #{qualityImageShareUrl,jdbcType=VARCHAR}


		AND STATUS = #{status,jdbcType=INTEGER}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND ORGANIZATION_ID = #{organizationId,jdbcType=BIGINT}


		AND SENDED = #{sended,jdbcType=VARCHAR}


		AND UPDATED = #{updated,jdbcType=VARCHAR}


		AND APPROVAL_OPINION = #{approvalOpinion,jdbcType=VARCHAR}

 WHERE DELETED = "F"
